SET NAMES utf8mb4;
USE job_manage;

CREATE TABLE IF NOT EXISTS `host_topo`  (
  `host_id` bigint(20) NOT NULL,
  `set_id` bigint(20) NOT NULL,
  `module_id` bigint(20) NOT NULL,
  PRIMARY KEY (`host_id`, `set_id`, `module_id`) USING BTREE,
  INDEX `idx_set_id`(`set_id`) USING BTREE,
  INDEX `idx_module_id`(`module_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci;


DROP PROCEDURE IF EXISTS job_schema_update;

DELIMITER <JOB_UBF>

CREATE PROCEDURE job_schema_update()
BEGIN

    DECLARE db VARCHAR(100);
    SET AUTOCOMMIT = 0;
    SELECT DATABASE() INTO db;

    IF NOT EXISTS(SELECT 1
                  FROM information_schema.COLUMNS
                  WHERE TABLE_SCHEMA = db
                    AND TABLE_NAME = 'notify_template'
                    AND COLUMN_NAME = 'is_default') THEN
        ALTER TABLE `notify_template` ADD COLUMN `is_default` BIT(1) DEFAULT 0;
    END IF;

    COMMIT;
END <JOB_UBF>
DELIMITER ;
CALL job_schema_update();

DROP PROCEDURE IF EXISTS job_schema_update;

BEGIN;

DELETE FROM `notify_template` WHERE id in (1,2,3,4,5,6,7,8) AND creator='admin';

REPLACE INTO `notify_template`(`id`, `code`, `name`, `channel`, `title`, `content`, `title_en`, `content_en`, `creator`, `last_modify_user`, `create_time`, `last_modify_time`, `is_default`) VALUES (1, 'beforeCronJobExecute', '定时任务执行前通知模板', 'mail', '【蓝鲸作业平台】定时任务提醒消息', '<table class=\"template-table\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; min-width: auto; mso-table-lspace: 0pt; mso-table-rspace: 0pt; background-color: #fff; background: #fff;\">\r\n	<tbody>\r\n		<tr>\r\n			<td align=\"center\" valign=\"top\" width=\"100%\" style=\"padding: 16px;\">\r\n			   <table class=\"template-table\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"956\" style=\"font-size: 14px; min-width: auto; mso-table-lspace: 0pt; mso-table-rspace: 0pt; padding-top:30px;\">\r\n					<tbody>\r\n						<tr>\r\n							<td valign=\"top\" align=\"center\" style=\"padding: 24px;\" bgcolor=\"#f9f8f6\">\r\n								<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt; border: 1px solid #e6e6e6;\">\r\n									<tr>\r\n										<td class=\"email-title\" style=\"padding: 20px 36px; line-height: 1.5; border-bottom: 1px solid #e6e6e6; background: #fff; font-size: 22px;\">【蓝鲸作业平台】定时任务提醒通知</td>\r\n									</tr>\r\n									<tr>\r\n										<td class=\"email-content\" style=\"padding: 0 36px; background: #fff;\">\r\n											<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt;\">\r\n												<tr>\r\n													<td class=\"email-source\" style=\"padding: 14px 0; color: #bebebe;\">来自 蓝鲸作业平台 的消息推送</td>\r\n												</tr>\r\n\r\n												<tr class=\"email-information\">\r\n													<td class=\"table-info\">\r\n														<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt;\">\r\n															<tr class=\"table-title\">\r\n																<td style=\"padding-top: 36px; padding-bottom: 14px; color: #707070;\">下列定时任务将在 <font style=\"font-weight:bold;color:red;\">{{ notify_time }}</font> 分钟后 <font style=\"font-weight:bold;\">开始执行</font>，请知悉：</td>\r\n															</tr>\r\n															<tr>\r\n																<td>\r\n																	<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt; border: 1px solid #e6e6e6; border-collapse: collapse;\">\r\n																		<tbody style=\"color: #707070;\">\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">任务名称</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"><a href=\"{{BASE_HOST}}{{cron_uri}}\" style=\"color: #3c96ff\"> {{ cron_name }}</a></td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">所属业务</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\">[{{ APP_ID }}] {{ APP_NAME }}</td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">设置人</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"> {{ cron_updater }}</td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">执行策略</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"> {{ cron_type }}</td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">策略详情</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"> {{ cron_rule }}</td>\r\n																			</tr>\r\n																		</tbody>\r\n																	</table>\r\n																</td>\r\n															</tr>\r\n														</table>\r\n													</td>\r\n												</tr>\r\n\r\n												<tr class=\"prompt-tips\">\r\n													<td style=\"padding-top: 32px; padding-bottom: 10px; color: #707070;\">如有任何疑问，请随时联系蓝鲸助手。</td>\r\n												</tr>\r\n												<tr class=\"info-remark\">\r\n													<td style=\"padding: 20px 0; text-align: right; line-height: 24px; color: #707070;\">\r\n														<div>{{ triggerTime }}</div>\r\n													</td>\r\n												</tr>\r\n											</table>\r\n										</td>\r\n									</tr>\r\n									<tr class=\"email-footer\">\r\n										<td style=\" padding: 20px 0 20px 36px; border-top: 1px solid #e6e6e6; background: #fff; color: #c7c7c7;\">你收到此邮件，是因为开启了该定时任务的执行前消息通知。</td>\r\n									</tr>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n			   </table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '【BlueKing JOB】Cron Pre-Launch Notification', '<table class=\"template-table\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; min-width: auto; mso-table-lspace: 0pt; mso-table-rspace: 0pt; background-color: #fff; background: #fff;\">\r\n	<tbody>\r\n		<tr>\r\n			<td align=\"center\" valign=\"top\" width=\"100%\" style=\"padding: 16px;\">\r\n			   <table class=\"template-table\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"956\" style=\"font-size: 14px; min-width: auto; mso-table-lspace: 0pt; mso-table-rspace: 0pt; padding-top:30px;\">\r\n					<tbody>\r\n						<tr>\r\n							<td valign=\"top\" align=\"center\" style=\"padding: 24px;\" bgcolor=\"#f9f8f6\">\r\n								<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt; border: 1px solid #e6e6e6;\">\r\n									<tr>\r\n										<td class=\"email-title\" style=\"padding: 20px 36px; line-height: 1.5; border-bottom: 1px solid #e6e6e6; background: #fff; font-size: 22px;\">【BlueKing JOB】Cron Pre-Launch Notification</td>\r\n									</tr>\r\n									<tr>\r\n										<td class=\"email-content\" style=\"padding: 0 36px; background: #fff;\">\r\n											<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt;\">\r\n												<tr>\r\n													<td class=\"email-source\" style=\"padding: 14px 0; color: #bebebe;\">This message is from <b>BlueKing JOB</b></td>\r\n												</tr>\r\n\r\n												<tr class=\"email-information\">\r\n													<td class=\"table-info\">\r\n														<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt;\">\r\n															<tr class=\"table-title\">\r\n																<td style=\"padding-top: 36px; padding-bottom: 14px; color: #707070;\">The Cron task below will <font style=\"font-weight:bold;\">launch automatically</font> after <font style=\"font-weight:bold;color:red;\">{{ notify_time }}</font> mins, please be noted!</td>\r\n															</tr>\r\n															<tr>\r\n																<td>\r\n																	<table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"font-size: 14px; mso-table-lspace: 0pt; mso-table-rspace: 0pt; border: 1px solid #e6e6e6; border-collapse: collapse;\">\r\n																		<tbody style=\"color: #707070;\">\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">TASK NAME</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"><a href=\"{{BASE_HOST}}{{cron_uri}}\" style=\"color: #3c96ff\"> {{ cron_name }}</a></td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">BUSINESS</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\">[{{ APP_ID }}] {{ APP_NAME }}</td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">OPERATOR</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"> {{ cron_updater }}</td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">REPEAT FREQUENCY</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"> {{ cron_type }}</td>\r\n																			</tr>\r\n																			<tr>\r\n																				<td width=\"30%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: right; font-weight: bold; background-color: #f3f3f3;\">TIME SET</td>\r\n																				<td width=\"70%\" style=\" padding: 16px; border: 1px solid #e6e6e6;text-align: left; font-weight: normal;\"> {{ cron_rule }}</td>\r\n																			</tr>\r\n																		</tbody>\r\n																	</table>\r\n																</td>\r\n															</tr>\r\n														</table>\r\n													</td>\r\n												</tr>\r\n\r\n												<tr class=\"prompt-tips\">\r\n													<td style=\"padding-top: 32px; padding-bottom: 10px; color: #707070;\">If You Have Any Questions or Concerns, Please Contact BK Assistant.</td>\r\n												</tr>\r\n												<tr class=\"info-remark\">\r\n													<td style=\"padding: 20px 0; text-align: right; line-height: 24px; color: #707070;\">\r\n														<div>{{ triggerTime }}</div>\r\n													</td>\r\n												</tr>\r\n											</table>\r\n										</td>\r\n									</tr>\r\n									<tr class=\"email-footer\">\r\n										<td style=\" padding: 20px 0 20px 36px; border-top: 1px solid #e6e6e6; background: #fff; color: #c7c7c7;\">Your name is in \"Notify To\" field of this Cron settings, that\'s why you received this message.</td>\r\n									</tr>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n			   </table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'admin', 'admin', 0, 0, b'1');
REPLACE INTO `notify_template`(`id`, `code`, `name`, `channel`, `title`, `content`, `title_en`, `content_en`, `creator`, `last_modify_user`, `create_time`, `last_modify_time`, `is_default`) VALUES (2, 'beforeCronJobExecute', '定时任务执行前通知模板', 'rtx', '【蓝鲸作业平台】定时任务执行前提醒', ' 下列定时任务将在{{ notify_time }}分钟后执行\r\n任务名称：{{ cron_name }}\r\n所属业务：{{ APP_NAME }}\r\n　设置人：{{ cron_updater }}\r\n执行策略：{{ cron_type }}\r\n策略详情：{{ cron_rule }}\r\n查看详情：{{BASE_HOST}}{{cron_uri}}', '【BlueKing JOB】Cron Pre-Launch Notification', 'The task will launch automatically after {{ notify_time }} mins, please be noted!\r\n    Task Name: {{ cron_name }}\r\n        Business: {{ APP_NAME }}\r\n       Operator: {{ cron_updater }}\r\nREPEAT FREQ: {{ cron_type }}\r\n       Time Set: {{ cron_rule }}\r\n          Details: {{BASE_HOST}}{{cron_uri}}', 'admin', 'admin', 0, 0, b'1');
REPLACE INTO `notify_template`(`id`, `code`, `name`, `channel`, `title`, `content`, `title_en`, `content_en`, `creator`, `last_modify_user`, `create_time`, `last_modify_time`, `is_default`) VALUES (3, 'beforeCronJobExecute', '定时任务执行前通知模板', 'sms', '定时任务执行前提醒：您的业务[{{ APP_NAME }}]有一个定时任务[{{ cron_name }}]将在{{ notify_time }}分钟后执行，请知悉。【蓝鲸作业平台】', '定时任务执行前提醒：您的业务[{{ APP_NAME }}]有一个定时任务[{{ cron_name }}]将在{{ notify_time }}分钟后执行，请知悉。【蓝鲸作业平台】', 'Cron Pre-Launch Notification: There\'s a task of [{{ APP_NAME }}] will launch automatically after {{ notify_time }} mins, more details: {{BASE_HOST}}{{cron_uri}}【BlueKing JOB】', 'Cron Pre-Launch Notification: There\'s a task of [{{ APP_NAME }}] will launch automatically after {{ notify_time }} mins, more details: {{BASE_HOST}}{{cron_uri}}【BlueKing JOB】', 'admin', 'admin', 0, 0, b'1');
